document.addEventListener("DOMContentLoaded",a=>{a&&console.info("DOM loaded");const b=document.querySelectorAll(".change-devour");b&&b.forEach(a=>{a.addEventListener("click",a=>{const b=a.target.getAttribute("data-id"),c=a.target.getAttribute("data-newdevour");fetch(`/api/burgers/${b}`,{method:"PUT",headers:{Accept:"application/json","Content-Type":"application/json"},body:JSON.stringify({devour:c})}).then(a=>{a.ok?(console.log(`changed sleep to: ${c}`),location.reload("/")):alert("something went wrong!")})})});const c=document.getElementById("create-form");c&&c.addEventListener("submit",a=>{a.preventDefault();const b={name:document.getElementById("ca").value.trim()};fetch("/api/burgers",{method:"POST",headers:{Accept:"application/json","Content-Type":"application/json"},body:JSON.stringify(b)}).then(()=>{document.getElementById("ca").value="",console.log("Created a new burger!"),location.reload()})});const d=document.querySelectorAll(".delete-burger");d.forEach(a=>{a.addEventListener("click",a=>{const b=a.target.getAttribute("data-id");fetch(`/api/burgers/${b}`,{method:"DELETE"}).then(a=>{console.log(a),console.log(`Deleted burger: ${b}`),location.reload()})})})});